import React , {useCallback ,useEffect, useState} from 'react'
import axios from 'axios'
import {Movie} from '../../templates'
import {context as c} from '../../context'

import Button from '@material-ui/core/Button';
import CameraIcon from '@material-ui/icons/PhotoCamera';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import CssBaseline from '@material-ui/core/CssBaseline';
import Grid from '@material-ui/core/Grid';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import {Link} from 'react-router-dom';

const useStyles = makeStyles((theme) => ({
  icon: {
    marginRight: theme.spacing(2),
  },
  heroContent: {
    backgroundColor: theme.palette.background.paper,
    padding: theme.spacing(8, 0, 6),
  },
  heroButtons: {
    marginTop: theme.spacing(4),
  },
  cardGrid: {
    paddingTop: theme.spacing(8),
    paddingBottom: theme.spacing(8),
    width: '100%',
    maxWidth: '2000px'
  },
  card: {
    height: '100%',
    display: 'flex',
    flexDirection: 'column',
  },
  cardMedia: {
    width: '100%',
    height: '50%',
    paddingTop: '150%'
  },
  cardContent: {
    flexGrow: 1,
  },
  footer: {
    backgroundColor: theme.palette.background.paper,
    padding: theme.spacing(6),
  },
}));

const cards = [1,2,3,4,5,6,7];

export default function MovieDetail() {
  const classes = useStyles();

  const [data, setData] = useState([])
  useEffect(() => {
      axios.get(`http://127.0.0.1:8080/api/movies`)
      .then(res=>{
          setData(res.data.slice(0, 102))
      })
      .catch(e=>{
          alert(`List Failure`)
          throw(e)
      })

  },[])

  const fetchSomeMovie = useCallback(async e=> {
    const title = document.querySelector('#Title').value
    alert(title)
    try {
        const req = {
            method: c.get,
            url: `${c.url}/api/movie/${title}`,
            auth: c.auth
        }
        const res = await axios(req)
        setData(res.data)
    } catch (error){
        alert(`The value could not be found. ${error}`)
    }
    },[])

    const fetchSomeRecommendMovie = useCallback(async e=> {
      const title_naver_eng = e.target.getAttribute(`title_naver_eng`)
      alert(`추천 영화 분석 중 입니다. 잠시만 기다려 주세요!`)
      try {
          const req = {
              method: c.get,
              url: `${c.url}/api/movie-recommendation/${title_naver_eng}`,
              auth: c.auth
          }
          const res = await axios(req)
          setData(res.data)
      } catch (error){
          alert(`The value could not be found. ${error}`)
      }
      },[])

  return (
    <React.Fragment>
      <CssBaseline />
      <main>
        <div>
          <nav>
            <ol>
                <li><Link to='/movie-register'>Movie Register</Link></li>
                <li><Link to='/movie-list'>Movie List</Link></li>
                <li><Link to='/movie-modify'>Movie Modify</Link></li>
                <li><Link to='/movie-remove'>Movie Remove</Link></li>
            </ol>
          </nav>

          제목 검색 : <input type="text" id='Title'/> 
          <button type="button" class="btn btn-sm btn-primary" id="btnSearch" onClick={fetchSomeMovie}>Search</button>
        <Container className={classes.cardGrid}> 
          <Grid container spacing={3}>
            {data.map((i, index) => (
              <Grid item key={index} xs={12} sm={6} md={2}>
                <Card className={classes.card}>
                  <CardMedia
                    className={classes.cardMedia}
                    image={i.image_naver}
                  />
                  <CardContent className={classes.cardContent}>
                    <Typography gutterBottom variant="h5" component="h2">
                     {i.title_kor}
                    </Typography>
                    <Typography>
                      {i.year_kor}년
                    </Typography>
                    <Typography>
                      {i.genres_kor}
                    </Typography>
                    <Typography>
                      {i.running_time_kor}분
                    </Typography>
                    <Typography>
                      {i.director_naver_kor}
                    </Typography>
                    <Typography>
                      {i.actor_naver_kor}
                    </Typography>
                  </CardContent>
                  <CardActions>
                    <button title_naver_eng={i.title_naver_eng} onClick={fetchSomeRecommendMovie}> 추천 영화 </button>
                  </CardActions>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Container>
        </div>  
      </main>
      {/* Footer */}
      <footer className={classes.footer}>
        <Typography variant="h6" align="center" gutterBottom>
          Footer
        </Typography>
        <Typography variant="subtitle1" align="center" color="textSecondary" component="p">
          Something here to give the footer a purpose!
        </Typography>
      </footer>
      {/* End footer */}
    </React.Fragment>

  );
}
